var logger = require("logger")(module);
var db = require("db")
db.connect();

function User(name) {
    this.name = name ? name : "Unknown user";
}

User.prototype.hello = function(){
    console.log("'%s' says: %s", this.name, db.getPhrase("Hello"));
};

module.exports.User = User;

